/span></em></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;Гарантийное письмо должно быть подписано и передано в редакцию в отсканированном виде</span></span></em><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>',17),(34,0,'en',NULL,NULL,17);
